
/*대상 클래스를 json으로 저장하기*/
module.exports.save = function(targetObject, path, filename){
  var fileStream = require('fs');
  var targetPath = path + "/" + filename + '.json';
  var jsonData = JSON.stringify(targetObject);
  fileStream.writeFileSync(targetPath, jsonData, 'utf-8');
};

/*대상 클래스를 json으로부터 로드하기*/
module.exports.load = function(path, filename){
  var fileStream = require('fs');
  var targetPath = path + "/" + filename + '.json';
  var jsonObject = JSON.parse(fileStream.readFileSync(targetPath, 'utf-8'));
  return jsonObject;
};
